import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Matches = () => {
  const [matches, setMatches] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMatches();
  }, []);

  const fetchMatches = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        window.location.href = '/';
        return;
      }

      const response = await axios.get('http://localhost:5000/api/user/matches', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMatches(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Matches fetch error:', error);
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        window.location.href = '/';
      }
      setLoading(false);
    }
  };

  const formatLastActive = (lastActive) => {
    const now = new Date();
    const lastActiveDate = new Date(lastActive);
    const diffInHours = Math.floor((now - lastActiveDate) / (1000 * 60 * 60));

    if (diffInHours < 1) return 'Active now';
    if (diffInHours < 24) return `Active ${diffInHours}h ago`;
    const diffInDays = Math.floor(diffInHours / 24);
    return `Active ${diffInDays}d ago`;
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/';
  };

  // Navigation Header Component
  const NavigationHeader = () => (
    <div className="flex flex-wrap items-center justify-between gap-4 mb-6">
      <h1 className="text-2xl font-bold text-white sm:text-3xl">Your Matches</h1>
      <div className="flex flex-wrap gap-2 sm:gap-4">
        <button
          onClick={() => window.location.href = '/dashboard'}
          className="flex items-center gap-2 px-3 py-2 text-white transition duration-200 border border-white rounded-lg sm:px-4 hover:bg-white hover:text-pink-500"
        >
          <span>üîç</span>
          <span className="hidden sm:inline">Find More</span>
        </button>
        <button
          onClick={() => window.location.href = '/messages'}
          className="flex items-center gap-2 px-3 py-2 text-white transition duration-200 border border-white rounded-lg sm:px-4 hover:bg-white hover:text-pink-500"
        >
          <span>üí¨</span>
          <span className="hidden sm:inline">Messages</span>
        </button>
        <button
          onClick={() => window.location.href = '/profile'}
          className="flex items-center gap-2 px-3 py-2 text-white transition duration-200 border border-white rounded-lg sm:px-4 hover:bg-white hover:text-pink-500"
        >
          <span>üë§</span>
          <span className="hidden sm:inline">Profile</span>
        </button>
        <button
          onClick={handleLogout}
          className="px-2 text-white transition duration-200 hover:text-gray-200"
        >
          Logout
        </button>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen p-4 bg-gradient-to-r from-pink-400 to-red-400">
        <NavigationHeader />
        <div className="flex items-center justify-center flex-1 min-h-[60vh]">
          <div className="text-center text-white">
            <div className="w-12 h-12 mx-auto mb-4 border-b-2 border-white rounded-full animate-spin"></div>
            <p className="text-xl">Loading your matches...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen p-4 bg-gradient-to-r from-pink-400 to-red-400">
      <NavigationHeader />

      <div className="mx-auto max-w-7xl">
        {matches.length === 0 ? (
          <div className="text-center text-white min-h-[60vh] flex items-center justify-center">
            <div>
              <div className="mb-6 text-6xl sm:text-8xl">üíî</div>
              <h2 className="mb-4 text-2xl font-bold sm:text-3xl">No matches yet!</h2>
              <p className="mb-6 text-lg sm:text-xl opacity-90">Keep swiping to find your perfect match</p>
              <button
                onClick={() => window.location.href = '/dashboard'}
                className="px-6 py-3 font-semibold text-pink-500 transition duration-200 bg-white rounded-lg shadow-lg sm:px-8 hover:bg-gray-100"
              >
                Start Matching
              </button>
            </div>
          </div>
        ) : (
          <>
            <div className="mb-6 text-center">
              <p className="text-lg text-white sm:text-xl opacity-90">
                üéâ You have {matches.length} amazing match{matches.length !== 1 ? 'es' : ''}!
              </p>
            </div>
            
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 sm:gap-6">
              {matches.map((match, index) => (
                <div 
                  key={match.userId._id || index} 
                  className="flex flex-col overflow-hidden transition-all duration-300 bg-white shadow-xl rounded-2xl hover:shadow-2xl hover:scale-105"
                >
                  {/* Photo Section */}
                  <div className="relative">
                    <div className="flex items-center justify-center w-full h-64 overflow-hidden sm:h-72 bg-gradient-to-br from-pink-200 to-purple-200">
                      {match.userId.photos && match.userId.photos.length > 0 ? (
                        <img
                          src={match.userId.photos.find(p => p.isMain)?.url || match.userId.photos[0].url}
                          alt={match.userId.firstName}
                          className="object-cover w-full h-full"
                        />
                      ) : (
                        <div className="text-6xl text-gray-400">üë§</div>
                      )}
                    </div>
                    
                    {/* Activity Status Badge */}
                    <div className="absolute px-2 py-1 text-xs font-semibold text-white bg-green-500 rounded-full shadow-lg top-3 right-3">
                      {formatLastActive(match.userId.lastActive)}
                    </div>
                    
                    {/* Age Badge */}
                    <div className="absolute px-2 py-1 text-xs font-semibold text-gray-800 bg-white rounded-full shadow-lg top-3 left-3 bg-opacity-90">
                      {match.userId.age}
                    </div>
                  </div>

                  {/* Content Section */}
                  <div className="flex flex-col flex-1 p-4">
                    <div className="flex-1">
                      <h3 className="mb-2 text-lg font-bold text-gray-800 truncate sm:text-xl">
                        {match.userId.firstName} {match.userId.lastName}
                      </h3>
                      
                      <div className="flex flex-wrap gap-2 mb-3">
                        <span className="px-2 py-1 text-xs font-medium text-purple-600 bg-purple-100 rounded-full">
                          {match.userId.major}
                        </span>
                        <span className="px-2 py-1 text-xs font-medium text-pink-600 bg-pink-100 rounded-full">
                          {match.userId.year}
                        </span>
                      </div>

                      {match.userId.bio && (
                        <p className="mb-3 text-sm text-gray-600 line-clamp-2">
                          {match.userId.bio}
                        </p>
                      )}

                      {match.userId.interests && match.userId.interests.length > 0 && (
                        <div className="flex flex-wrap gap-1 mb-3">
                          {match.userId.interests.slice(0, 3).map((interest, idx) => (
                            <span key={idx} className="px-2 py-1 text-xs text-blue-600 bg-blue-100 rounded-full">
                              {interest}
                            </span>
                          ))}
                          {match.userId.interests.length > 3 && (
                            <span className="text-xs text-gray-500">+{match.userId.interests.length - 3} more</span>
                          )}
                        </div>
                      )}

                      <div className="mb-3 text-xs text-gray-500">
                        üíï Matched on {new Date(match.matchedAt).toLocaleDateString()}
                      </div>
                    </div>

                    {/* Action Button */}
                    <button
                      onClick={() => window.location.href = `/messages/${match.userId._id}`}
                      className="w-full py-3 font-semibold text-white transition-all duration-200 transform rounded-lg shadow-lg bg-gradient-to-r from-pink-500 to-red-500 hover:from-pink-600 hover:to-red-600 hover:shadow-xl hover:scale-105"
                    >
                      <span className="flex items-center justify-center gap-2">
                        <span>üí¨</span>
                        <span>Send Message</span>
                      </span>
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default Matches;
