import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [matches, setMatches] = useState([]);
  const [currentMatch, setCurrentMatch] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchMatches();
  }, []);

  const fetchMatches = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        window.location.href = '/';
        return;
      }

      const response = await axios.get('http://localhost:5000/api/user/potential-matches', {
        headers: { Authorization: `Bearer ${token}` }
      });

      setMatches(response.data);
      setLoading(false);
    } catch (error) {
      setError('Error loading matches. Please try again.');
      setLoading(false);

      // If unauthorized, redirect to login
      if (error.response?.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        window.location.href = '/';
      }
    }
  };

  const handleAction = async (action) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/user/action', {
        targetUserId: matches[currentMatch]._id,
        action
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.data.match) {
        alert('üéâ It\'s a match! You can now start chatting!');
      }

      // Move to next match
      setCurrentMatch(currentMatch + 1);
    } catch (error) {
      console.error('Error:', error);
      setError('Action failed. Please try again.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    window.location.href = '/';
  };

  // Navigation Header Component (shared across all states)
  const NavigationHeader = () => (
    <div className="flex items-center justify-between mb-4">
      <h1 className="text-2xl font-bold text-white">UoH Dating</h1>
      <div className="flex space-x-4">
        <button 
          onClick={() => window.location.href = '/matches'}
          className="flex items-center px-4 py-2 space-x-2 text-white transition duration-200 border border-white rounded-lg hover:text-gray-200"
        >
          <span>üíï</span>
          <span>Matches</span>
        </button>
        <button 
          onClick={() => window.location.href = '/messages'}
          className="flex items-center px-4 py-2 space-x-2 text-white transition duration-200 border border-white rounded-lg hover:text-gray-200"
        >
          <span>üí¨</span>
          <span>Messages</span>
        </button>
        <button 
          onClick={() => window.location.href = '/profile'}
          className="flex items-center px-4 py-2 space-x-2 text-white transition duration-200 border border-white rounded-lg hover:text-gray-200"
        >
          <span>üë§</span>
          <span>Profile</span>
        </button>
        <button 
          onClick={handleLogout}
          className="text-white transition duration-200 hover:text-gray-200"
        >
          Logout
        </button>
      </div>
    </div>
  );

  if (loading) {
    return (
      <div className="min-h-screen p-4 bg-gradient-to-r from-pink-400 to-red-400">
        <NavigationHeader />
        <div className="flex items-center justify-center flex-1">
          <div className="text-center text-white">
            <div className="w-12 h-12 mx-auto mb-4 border-b-2 border-white rounded-full animate-spin"></div>
            <p className="text-xl">Finding your perfect matches...</p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen p-4 bg-gradient-to-r from-red-400 to-pink-400">
        <NavigationHeader />
        <div className="flex items-center justify-center flex-1">
          <div className="text-center text-white">
            <h2 className="mb-4 text-3xl font-bold">Oops!</h2>
            <p className="mb-6 text-xl">{error}</p>
            <button
              onClick={() => window.location.reload()}
              className="px-6 py-3 font-medium text-red-500 transition duration-200 bg-white rounded-lg hover:bg-gray-100"
            >
              Try Again
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (matches.length === 0 || currentMatch >= matches.length) {
    return (
      <div className="min-h-screen p-4 bg-gradient-to-r from-blue-500 to-purple-500">
        <NavigationHeader />
        <div className="flex items-center justify-center flex-1">
          <div className="text-center text-white">
            <div className="mb-4 text-6xl">üíî</div>
            <h2 className="mb-4 text-3xl font-bold">No more matches!</h2>
            <p className="mb-6 text-xl">Check back later for new potential matches</p>
            <div className="space-x-4">
              <button
                onClick={() => window.location.reload()}
                className="px-6 py-3 font-medium text-purple-500 transition duration-200 bg-white rounded-lg hover:bg-gray-100"
              >
                Refresh
              </button>
              <button
                onClick={() => window.location.href = '/matches'}
                className="px-6 py-3 font-medium text-white transition duration-200 bg-transparent border-2 border-white rounded-lg hover:bg-white hover:text-purple-500"
              >
                View Matches
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  const match = matches[currentMatch];

  return (
    <div className="min-h-screen p-4 bg-gradient-to-r from-pink-400 to-red-400">
      <NavigationHeader />

      {/* Match Counter */}
      <div className="mb-4 text-center">
        <p className="text-lg text-white">
          {currentMatch + 1} of {matches.length} potential matches
        </p>
      </div>

      {/* Match Card */}
      <div className="flex items-center justify-center">
        <div className="w-full max-w-sm mx-4 overflow-hidden transition duration-300 transform bg-white shadow-2xl rounded-2xl hover:scale-105">
          {/* Photo Section */}
          <div className="relative flex items-center justify-center h-96 bg-gradient-to-br from-pink-200 to-purple-200">
            {match.photos && match.photos.length > 0 ? (
              <img
                src={match.photos[0].url}
                alt={match.firstName}
                className="object-cover w-full h-full"
              />
            ) : (
              <div className="text-center">
                <div className="mb-2 text-8xl">üë§</div>
                <p className="text-gray-500">No photo available</p>
              </div>
            )}

            {/* Age badge */}
            <div className="absolute px-3 py-1 bg-white rounded-full top-4 right-4 bg-opacity-90">
              <span className="text-sm font-semibold text-gray-800">{match.age}</span>
            </div>
          </div>

          {/* Info Section */}
          <div className="p-6">
            <div className="mb-4 text-center">
              <h2 className="mb-2 text-3xl font-bold text-gray-800">
                {match.firstName} {match.lastName}
              </h2>
              <div className="flex items-center justify-center mb-3 space-x-4 text-sm text-gray-600">
                <span className="px-3 py-1 bg-gray-100 rounded-full">{match.year}</span>
                <span className="px-3 py-1 bg-gray-100 rounded-full">{match.major}</span>
              </div>
            </div>

            {match.bio && (
              <div className="mb-6">
                <p className="italic text-center text-gray-700">"{match.bio}"</p>
              </div>
            )}

            {match.interests && match.interests.length > 0 && (
              <div className="mb-6">
                <h4 className="mb-2 text-sm font-semibold text-center text-gray-600">Interests:</h4>
                <div className="flex flex-wrap justify-center gap-2">
                  {match.interests.slice(0, 4).map((interest, index) => (
                    <span key={index} className="px-2 py-1 text-xs text-pink-600 bg-pink-100 rounded-full">
                      {interest}
                    </span>
                  ))}
                </div>
              </div>
            )}

            {/* Action Buttons */}
            <div className="flex justify-center mt-6 space-x-6">
              <button
                onClick={() => handleAction('dislike')}
                className="flex items-center px-8 py-4 space-x-2 text-lg font-semibold text-white transition duration-200 transform bg-red-500 rounded-full shadow-lg hover:bg-red-600 hover:scale-110"
              >
                <span>‚ùå</span>
                <span>Pass</span>
              </button>

              <button
                onClick={() => handleAction('like')}
                className="flex items-center px-8 py-4 space-x-2 text-lg font-semibold text-white transition duration-200 transform bg-green-500 rounded-full shadow-lg hover:bg-green-600 hover:scale-110"
              >
                <span>üíñ</span>
                <span>Like</span>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Instructions */}
      <div className="mt-6 text-center">
        <p className="text-sm text-white opacity-75">
          Swipe or click the buttons to find your perfect match!
        </p>
      </div>
    </div>
  );
};

export default Dashboard;
