import React, { useState, useEffect } from 'react';
import { userAPI, messageAPI } from '../services/api';

const Matches = () => {
  const [matches, setMatches] = useState([]);
  const [selectedMatch, setSelectedMatch] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchMatches();
  }, []);

  const fetchMatches = async () => {
    try {
      const response = await userAPI.getMatches();
      setMatches(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching matches:', error);
      setLoading(false);
    }
  };

  const fetchMessages = async (userId) => {
    try {
      const response = await messageAPI.getMessages(userId);
      setMessages(response.data);
    } catch (error) {
      console.error('Error fetching messages:', error);
    }
  };

  const sendMessage = async () => {
    if (!newMessage.trim() || !selectedMatch) return;

    try {
      await messageAPI.sendMessage(selectedMatch.userId._id, newMessage);
      setNewMessage('');
      fetchMessages(selectedMatch.userId._id);
    } catch (error) {
      console.error('Error sending message:', error);
    }
  };

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center">Loading matches...</div>;
  }

  return (
    <div className="min-h-screen bg-gray-100 flex">
      {/* Matches List */}
      <div className="w-1/3 bg-white border-r">
        <div className="p-4 bg-pink-500 text-white">
          <h2 className="text-xl font-bold">Your Matches ({matches.length})</h2>
        </div>
        <div className="overflow-y-auto">
          {matches.map((match) => (
            <div
              key={match._id}
              className={`p-4 border-b cursor-pointer hover:bg-gray-50 ${selectedMatch?._id === match._id ? 'bg-blue-50' : ''}`}
              onClick={() => {
                setSelectedMatch(match);
                fetchMessages(match.userId._id);
              }}
            >
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 rounded-full bg-gray-200 overflow-hidden">
                  {match.userId.photos?.length > 0 ? (
                    <img src={match.userId.photos[0].url} alt="" className="w-full h-full object-cover" />
                  ) : (
                    <div className="w-full h-full flex items-center justify-center text-2xl">ðŸ‘¤</div>
                  )}
                </div>
                <div>
                  <h3 className="font-semibold">{match.userId.firstName} {match.userId.lastName}</h3>
                  <p className="text-sm text-gray-500">Matched {new Date(match.matchedAt).toLocaleDateString()}</p>
                  <p className="text-xs text-gray-400">{match.userId.major}</p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Chat Area */}
      <div className="flex-1 flex flex-col">
        {selectedMatch ? (
          <>
            {/* Chat Header */}
            <div className="p-4 bg-white border-b flex items-center space-x-3">
              <div className="w-10 h-10 rounded-full bg-gray-200 overflow-hidden">
                {selectedMatch.userId.photos?.length > 0 ? (
                  <img src={selectedMatch.userId.photos[0].url} alt="" className="w-full h-full object-cover" />
                ) : (
                  <div className="w-full h-full flex items-center justify-center text-xl">ðŸ‘¤</div>
                )}
              </div>
              <div>
                <h3 className="font-semibold">{selectedMatch.userId.firstName} {selectedMatch.userId.lastName}</h3>
                <p className="text-sm text-gray-500">Last seen: {new Date(selectedMatch.userId.lastActive).toLocaleDateString()}</p>
              </div>
            </div>

            {/* Messages */}
            <div className="flex-1 overflow-y-auto p-4 space-y-4">
              {messages.map((message) => (
                <div key={message._id} className={`flex ${message.sender._id === JSON.parse(localStorage.getItem('user')).id ? 'justify-end' : 'justify-start'}`}>
                  <div className={`max-w-xs px-4 py-2 rounded-lg ${message.sender._id === JSON.parse(localStorage.getItem('user')).id ? 'bg-pink-500 text-white' : 'bg-gray-200'}`}>
                    <p>{message.content}</p>
                    <p className="text-xs opacity-75 mt-1">{new Date(message.createdAt).toLocaleTimeString()}</p>
                  </div>
                </div>
              ))}
            </div>

            {/* Message Input */}
            <div className="p-4 bg-white border-t">
              <div className="flex space-x-2">
                <input
                  type="text"
                  value={newMessage}
                  onChange={(e) => setNewMessage(e.target.value)}
                  placeholder="Type a message..."
                  className="flex-1 border rounded-lg px-4 py-2"
                  onKeyPress={(e) => e.key === 'Enter' && sendMessage()}
                />
                <button
                  onClick={sendMessage}
                  className="bg-pink-500 text-white px-6 py-2 rounded-lg hover:bg-pink-600"
                >
                  Send
                </button>
              </div>
            </div>
          </>
        ) : (
          <div className="flex-1 flex items-center justify-center bg-gray-50">
            <div className="text-center">
              <div className="text-6xl mb-4">ðŸ’¬</div>
              <h3 className="text-xl font-semibold text-gray-600 mb-2">Select a match to start chatting</h3>
              <p className="text-gray-500">Choose someone from your matches to begin a conversation</p>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Matches;
