import React, { useState } from 'react';
import axios from 'axios';

const Register = () => {
  const [formData, setFormData] = useState({
    email: '', password: '', firstName: '', lastName: '',
    age: '', major: '', year: '', location: ''
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/auth/register', formData);
      alert('Registration successful! Check your email for verification.');
      window.location.href = '/';
    } catch (error) {
      alert('Registration failed');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-purple-500 to-pink-500 flex items-center justify-center">
      <form onSubmit={handleSubmit} className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
        <h2 className="text-2xl font-bold mb-6 text-center">Join University Dating</h2>
        <div className="grid grid-cols-2 gap-4">
          <input type="text" placeholder="First Name" className="p-3 border rounded"
            onChange={(e) => setFormData({...formData, firstName: e.target.value})} />
          <input type="text" placeholder="Last Name" className="p-3 border rounded"
            onChange={(e) => setFormData({...formData, lastName: e.target.value})} />
        </div>
        <input type="email" placeholder="University Email (.edu)" className="w-full p-3 border rounded mb-4"
          onChange={(e) => setFormData({...formData, email: e.target.value})} />
        <input type="password" placeholder="Password" className="w-full p-3 border rounded mb-4"
          onChange={(e) => setFormData({...formData, password: e.target.value})} />
        <div className="grid grid-cols-2 gap-4 mb-4">
          <input type="number" placeholder="Age" className="p-3 border rounded"
            onChange={(e) => setFormData({...formData, age: e.target.value})} />
          <select className="p-3 border rounded"
            onChange={(e) => setFormData({...formData, year: e.target.value})}>
            <option value="">Select Year</option>
            <option value="Freshman">Freshman</option>
            <option value="Sophomore">Sophomore</option>
            <option value="Junior">Junior</option>
            <option value="Senior">Senior</option>
            <option value="Graduate">Graduate</option>
          </select>
        </div>
        <input type="text" placeholder="Major" className="w-full p-3 border rounded mb-4"
          onChange={(e) => setFormData({...formData, major: e.target.value})} />
        <input type="text" placeholder="Location/Campus" className="w-full p-3 border rounded mb-4"
          onChange={(e) => setFormData({...formData, location: e.target.value})} />
        <button type="submit" className="w-full bg-purple-500 text-white p-3 rounded hover:bg-purple-600">
          Sign Up</button>
        <p className="text-center mt-4">Already have an account? <a href="/" className="text-purple-500">Login</a></p>
      </form>
    </div>
  );
};

export default Register;
