import React, { useState, useEffect } from 'react';
import { userAPI } from '../services/api';

const Profile = () => {
  const [profile, setProfile] = useState(null);
  const [editing, setEditing] = useState(false);
  const [formData, setFormData] = useState({});
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchProfile();
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await userAPI.getProfile();
      setProfile(response.data);
      setFormData(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching profile:', error);
      setLoading(false);
    }
  };

  const handlePhotoUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    setUploading(true);
    const formData = new FormData();
    formData.append('photo', file);

    try {
      await userAPI.uploadPhoto(formData);
      await fetchProfile(); // Refresh profile
      alert('Photo uploaded successfully!');
    } catch (error) {
      alert('Photo upload failed. Please try again.');
    } finally {
      setUploading(false);
    }
  };

  const handleSave = async () => {
    try {
      await userAPI.updateProfile({
        bio: formData.bio,
        interests: formData.interests,
        major: formData.major,
        year: formData.year,
        location: formData.location
      });
      setProfile({ ...profile, ...formData });
      setEditing(false);
      alert('Profile updated successfully!');
    } catch (error) {
      alert('Update failed. Please try again.');
    }
  };

  if (loading) {
    return <div className="flex items-center justify-center min-h-screen">Loading...</div>;
  }

  // Add null check for profile
  if (!profile) {
    return <div className="flex items-center justify-center min-h-screen">Profile not found.</div>;
  }

  return (
    <div className="min-h-screen p-4 bg-gradient-to-r from-purple-400 to-pink-400">
      <div className="max-w-2xl mx-auto overflow-hidden bg-white rounded-lg shadow-2xl">
        <div className="p-6">
          <h1 className="mb-6 text-3xl font-bold text-center">My Profile</h1>

          {/* Photo Section */}
          <div className="mb-6 text-center">
            <div className="w-32 h-32 mx-auto mb-4 overflow-hidden bg-gray-200 rounded-full">
              {profile?.photos?.length > 0 ? (
                <img src={profile.photos[0].url} alt="Profile" className="object-cover w-full h-full" />
              ) : (
                <div className="flex items-center justify-center w-full h-full text-6xl">ðŸ‘¤</div>
              )}
            </div>
            <label className="px-4 py-2 text-white bg-blue-500 rounded cursor-pointer hover:bg-blue-600">
              {uploading ? 'Uploading...' : 'Upload Photo'}
              <input type="file" accept="image/*" onChange={handlePhotoUpload} className="hidden" disabled={uploading} />
            </label>
          </div>

          {/* Profile Info */}
          <div className="space-y-4">
            <div>
              <h2 className="text-2xl font-bold">{profile?.firstName} {profile?.lastName}</h2>
              <p className="text-gray-600">{profile?.email}</p>
            </div>

            {editing ? (
              <div className="space-y-4">
                <textarea
                  className="w-full p-3 border rounded"
                  placeholder="Tell others about yourself..."
                  value={formData?.bio || ''}
                  onChange={(e) => setFormData({...formData, bio: e.target.value})}
                />
                <input
                  className="w-full p-3 border rounded"
                  placeholder="Interests (comma separated)"
                  value={formData?.interests?.join(', ') || ''}
                  onChange={(e) => setFormData({...formData, interests: e.target.value.split(', ')})}
                />
                <div className="flex space-x-4">
                  <button onClick={handleSave} className="px-4 py-2 text-white bg-green-500 rounded">Save</button>
                  <button onClick={() => setEditing(false)} className="px-4 py-2 text-white bg-gray-500 rounded">Cancel</button>
                </div>
              </div>
            ) : (
              <div>
                <p className="mb-4 text-gray-700">{profile?.bio || 'No bio yet'}</p>
                <div className="flex flex-wrap gap-2 mb-4">
                  {profile?.interests?.map((interest, index) => (
                    <span key={index} className="px-2 py-1 text-blue-600 bg-blue-100 rounded">{interest}</span>
                  ))}
                </div>
                <button onClick={() => setEditing(true)} className="px-4 py-2 text-white bg-blue-500 rounded">Edit Profile</button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
