import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import io from 'socket.io-client';

const Messages = () => {
    const { userId } = useParams();
    const [matches, setMatches] = useState([]);
    const [selectedMatch, setSelectedMatch] = useState(null);
    const [messages, setMessages] = useState([]);
    const [newMessage, setNewMessage] = useState('');
    const [loading, setLoading] = useState(true);
    const [socket, setSocket] = useState(null);
    const [currentUser, setCurrentUser] = useState(null);
    const [showSidebar, setShowSidebar] = useState(true);

    useEffect(() => {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        setCurrentUser(user);

        // Initialize socket connection
        const newSocket = io('http://localhost:5000');
        setSocket(newSocket);

        newSocket.emit('join', user.id);

        newSocket.on('receive_message', (message) => {
            setMessages(prev => [...prev, message]);
        });

        fetchMatches();

        return () => newSocket.close();
    }, []);

    useEffect(() => {
        if (userId && matches.length > 0) {
            const match = matches.find(m => m.userId._id === userId);
            if (match) {
                setSelectedMatch(match);
                fetchMessages(userId);
                // Hide sidebar on mobile when a match is selected
                if (window.innerWidth < 768) {
                    setShowSidebar(false);
                }
            }
        }
    }, [userId, matches]);

    const fetchMatches = async () => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get('http://localhost:5000/api/user/matches', {
                headers: { Authorization: `Bearer ${token}` }
            });
            setMatches(response.data);
            setLoading(false);
        } catch (error) {
            console.error('Matches fetch error:', error);
            setLoading(false);
        }
    };

    const fetchMessages = async (otherUserId) => {
        try {
            const token = localStorage.getItem('token');
            const response = await axios.get(`http://localhost:5000/api/messages/${otherUserId}`, {
                headers: { Authorization: `Bearer ${token}` }
            });
            setMessages(response.data);
        } catch (error) {
            console.error('Messages fetch error:', error);
            if (error.response?.status === 403) {
                alert('You can only message matched users');
            }
        }
    };

    const sendMessage = async (e) => {
        e.preventDefault();
        if (!newMessage.trim() || !selectedMatch) return;

        try {
            const token = localStorage.getItem('token');
            const messageData = {
                receiverId: selectedMatch.userId._id,
                content: newMessage.trim(),
                messageType: 'text'
            };

            const response = await axios.post('http://localhost:5000/api/messages', messageData, {
                headers: { Authorization: `Bearer ${token}` }
            });

            setMessages(prev => [...prev, response.data]);

            // Emit to socket for real-time
            socket.emit('send_message', {
                ...response.data,
                receiverId: selectedMatch.userId._id
            });

            setNewMessage('');
        } catch (error) {
            console.error('Send message error:', error);
            if (error.response?.status === 403) {
                alert('You can only message matched users');
            } else {
                alert('Failed to send message');
            }
        }
    };

    const handleSelectMatch = (match) => {
        setSelectedMatch(match);
        fetchMessages(match.userId._id);
        window.history.pushState(null, '', `/messages/${match.userId._id}`);
        // Hide sidebar on mobile when a match is selected
        if (window.innerWidth < 768) {
            setShowSidebar(false);
        }
    };

    const handleBackToMatches = () => {
        setShowSidebar(true);
        setSelectedMatch(null);
        window.history.pushState(null, '', '/messages');
    };

    const handleLogout = () => {
        localStorage.removeItem('token');
        localStorage.removeItem('user');
        window.location.href = '/';
    };

    // Navigation Header Component
    const NavigationHeader = () => (
        <div className="flex items-center justify-between p-4 bg-gradient-to-r from-blue-500 to-purple-500">
            <div className="flex items-center space-x-3">
                <h2 className="text-lg sm:text-xl font-bold text-white">Messages</h2>
            </div>
            <div className="flex items-center space-x-2">
                <button
                    onClick={() => window.location.href = '/dashboard'}
                    className="flex items-center space-x-1 px-2 sm:px-3 py-1 text-xs sm:text-sm text-white border border-white rounded hover:bg-white hover:text-blue-500 transition duration-200"
                >
                    <span>🏠</span>
                    <span className="hidden sm:inline">Dashboard</span>
                </button>
                <button
                    onClick={() => window.location.href = '/matches'}
                    className="flex items-center space-x-1 px-2 sm:px-3 py-1 text-xs sm:text-sm text-white border border-white rounded hover:bg-white hover:text-blue-500 transition duration-200"
                >
                    <span>💕</span>
                    <span className="hidden sm:inline">Matches</span>
                </button>
                <button
                    onClick={() => window.location.href = '/profile'}
                    className="flex items-center space-x-1 px-2 sm:px-3 py-1 text-xs sm:text-sm text-white border border-white rounded hover:bg-white hover:text-blue-500 transition duration-200"
                >
                    <span>👤</span>
                    <span className="hidden sm:inline">Profile</span>
                </button>
                <button
                    onClick={handleLogout}
                    className="text-white hover:text-gray-200 transition duration-200 text-xs sm:text-sm px-1"
                >
                    Logout
                </button>
            </div>
        </div>
    );

    if (loading) {
        return (
            <div className="flex items-center justify-center min-h-screen bg-gradient-to-r from-blue-400 to-purple-400">
                <div className="text-center text-white">
                    <div className="w-8 h-8 sm:w-12 sm:h-12 mx-auto mb-4 border-b-2 border-white rounded-full animate-spin"></div>
                    <p className="text-lg sm:text-xl">Loading messages...</p>
                </div>
            </div>
        );
    }

    return (
        <div className="min-h-screen bg-gradient-to-r from-blue-400 to-purple-400">
            <NavigationHeader />
            
            <div className="flex h-[calc(100vh-80px)]">
                {/* Sidebar - Matches List */}
                <div className={`${
                    showSidebar ? 'block' : 'hidden'
                } md:block w-full md:w-1/3 overflow-y-auto bg-white border-r`}>
                    
                    <div className="p-4">
                        {matches.length === 0 ? (
                            <div className="py-8 text-center text-gray-500">
                                <div className="mb-2 text-4xl">💬</div>
                                <p>No matches to message yet</p>
                                <button
                                    onClick={() => window.location.href = '/dashboard'}
                                    className="mt-4 px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition duration-200"
                                >
                                    Find Matches
                                </button>
                            </div>
                        ) : (
                            <div className="space-y-3">
                                {matches.map((match, index) => (
                                    <div
                                        key={index}
                                        onClick={() => handleSelectMatch(match)}
                                        className={`p-3 rounded-lg cursor-pointer transition duration-200 ${
                                            selectedMatch?.userId._id === match.userId._id
                                                ? 'bg-purple-100 border-2 border-purple-300'
                                                : 'hover:bg-gray-100'
                                        }`}
                                    >
                                        <div className="flex items-center space-x-3">
                                            <div className="flex items-center justify-center w-12 h-12 overflow-hidden bg-gray-200 rounded-full">
                                                {match.userId.photos && match.userId.photos.length > 0 ? (
                                                    <img
                                                        src={match.userId.photos.find(p => p.isMain)?.url || match.userId.photos[0].url}
                                                        alt={match.userId.firstName}
                                                        className="object-cover w-full h-full"
                                                    />
                                                ) : (
                                                    <span className="text-2xl">👤</span>
                                                )}
                                            </div>
                                            <div className="flex-1">
                                                <h3 className="font-semibold">
                                                    {match.userId.firstName} {match.userId.lastName}
                                                </h3>
                                                <p className="text-sm text-gray-500">{match.userId.major}</p>
                                            </div>
                                        </div>
                                    </div>
                                ))}
                            </div>
                        )}
                    </div>
                </div>

                {/* Main Chat Area */}
                <div className={`${
                    !showSidebar ? 'block' : 'hidden'
                } md:block flex flex-col flex-1`}>
                    {selectedMatch ? (
                        <>
                            {/* Chat Header with Back Button */}
                            <div className="flex items-center p-3 sm:p-4 space-x-3 bg-white border-b">
                                {/* Mobile Back Button */}
                                <button
                                    onClick={handleBackToMatches}
                                    className="md:hidden flex items-center justify-center w-8 h-8 text-blue-500 hover:bg-blue-50 rounded-full transition duration-200"
                                >
                                    <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 19l-7-7 7-7"></path>
                                    </svg>
                                </button>
                                
                                <div className="flex items-center justify-center w-8 h-8 sm:w-10 sm:h-10 overflow-hidden bg-gray-200 rounded-full">
                                    {selectedMatch.userId.photos && selectedMatch.userId.photos.length > 0 ? (
                                        <img
                                            src={selectedMatch.userId.photos.find(p => p.isMain)?.url || selectedMatch.userId.photos[0].url}
                                            alt={selectedMatch.userId.firstName}
                                            className="object-cover w-full h-full"
                                        />
                                    ) : (
                                        <span className="text-lg sm:text-xl">👤</span>
                                    )}
                                </div>
                                <div>
                                    <h3 className="text-sm sm:text-lg font-semibold">
                                        {selectedMatch.userId.firstName} {selectedMatch.userId.lastName}
                                    </h3>
                                    <p className="text-xs sm:text-sm text-gray-500">
                                        {selectedMatch.userId.major} • {selectedMatch.userId.year}
                                    </p>
                                </div>
                            </div>

                            {/* Messages Area */}
                            <div className="flex-1 p-3 sm:p-4 space-y-3 sm:space-y-4 overflow-y-auto bg-gradient-to-b from-blue-50 to-purple-50">
                                {messages.length === 0 ? (
                                    <div className="py-8 text-center text-gray-700">
                                        <div className="mb-4 text-4xl sm:text-6xl">💬</div>
                                        <h3 className="mb-2 text-lg sm:text-xl font-semibold">Start the conversation!</h3>
                                        <p className="text-sm sm:text-base">You matched with {selectedMatch.userId.firstName}. Say hello!</p>
                                    </div>
                                ) : (
                                    messages.map((message, index) => (
                                        <div
                                            key={message._id || index}
                                            className={`flex ${message.sender._id === currentUser.id ? 'justify-end' : 'justify-start'}`}
                                        >
                                            <div
                                                className={`max-w-xs sm:max-w-sm lg:max-w-md px-3 sm:px-4 py-2 rounded-lg ${
                                                    message.sender._id === currentUser.id
                                                        ? 'bg-purple-500 text-white'
                                                        : 'bg-white text-gray-800 shadow-md'
                                                }`}
                                            >
                                                <p className="text-sm sm:text-base">{message.content}</p>
                                                <div className="flex items-center justify-between mt-1">
                                                    <p className={`text-xs ${
                                                        message.sender._id === currentUser.id ? 'text-purple-200' : 'text-gray-500'
                                                    }`}>
                                                        {new Date(message.createdAt).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' })}
                                                    </p>
                                                    {message.sender._id === currentUser.id && (
                                                        <span className={`text-xs ml-2 ${message.isRead ? 'text-purple-200' : 'text-purple-300'}`}>
                                                            {message.isRead ? '✓✓' : '✓'}
                                                        </span>
                                                    )}
                                                </div>
                                            </div>
                                        </div>
                                    ))
                                )}
                            </div>

                            {/* Message Input */}
                            <div className="p-3 sm:p-4 bg-white border-t">
                                <form onSubmit={sendMessage} className="flex space-x-2">
                                    <input
                                        type="text"
                                        value={newMessage}
                                        onChange={(e) => setNewMessage(e.target.value)}
                                        placeholder={`Message ${selectedMatch.userId.firstName}...`}
                                        className="flex-1 p-2 sm:p-3 text-sm sm:text-base border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent"
                                    />
                                    <button
                                        type="submit"
                                        disabled={!newMessage.trim()}
                                        className="px-4 sm:px-6 py-2 sm:py-3 text-sm sm:text-base text-white transition duration-200 bg-purple-500 rounded-lg hover:bg-purple-600 disabled:opacity-50 disabled:cursor-not-allowed"
                                    >
                                        Send
                                    </button>
                                </form>
                            </div>
                        </>
                    ) : (
                        <div className="flex items-center justify-center flex-1 text-gray-700">
                            <div className="text-center p-4">
                                <div className="mb-4 text-6xl sm:text-8xl">💬</div>
                                <h2 className="mb-2 text-xl sm:text-2xl font-bold">Select a match to start chatting</h2>
                                <p className="text-sm sm:text-lg">Choose someone from your matches to begin a conversation</p>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default Messages;
