import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';

const Navigation = () => {
  const navigate = useNavigate();
  const location = useLocation();

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    navigate('/');
  };

  const navItems = [
    { path: '/dashboard', label: 'Discover', icon: 'ðŸ’–' },
    { path: '/matches', label: 'Matches', icon: 'ðŸ’¬' },
    { path: '/profile', label: 'Profile', icon: 'ðŸ‘¤' }
  ];

  return (
    <div className="bg-white shadow-lg border-t">
      <div className="flex justify-around items-center py-2">
        {navItems.map((item) => (
          <button
            key={item.path}
            onClick={() => navigate(item.path)}
            className={`flex flex-col items-center p-3 rounded-lg transition duration-200 ${
              location.pathname === item.path ? 'bg-pink-100 text-pink-600' : 'text-gray-600 hover:text-pink-500'
            }`}
          >
            <span className="text-2xl mb-1">{item.icon}</span>
            <span className="text-xs font-medium">{item.label}</span>
          </button>
        ))}
        <button
          onClick={handleLogout}
          className="flex flex-col items-center p-3 rounded-lg text-gray-600 hover:text-red-500 transition duration-200"
        >
          <span className="text-2xl mb-1">ðŸšª</span>
          <span className="text-xs font-medium">Logout</span>
        </button>
      </div>
    </div>
  );
};

export default Navigation;
