import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Dashboard = () => {
  const [matches, setMatches] = useState([]);
  const [currentMatch, setCurrentMatch] = useState(0);

  useEffect(() => {
    fetchMatches();
  }, []);

  const fetchMatches = async () => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get('http://localhost:5000/api/user/potential-matches', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMatches(response.data);
    } catch (error) {
      console.error('Error fetching matches:', error);
    }
  };

  const handleAction = async (action) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.post('http://localhost:5000/api/user/action', {
        targetUserId: matches[currentMatch]._id,
        action
      }, {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (response.data.match) {
        alert('It\'s a match! üéâ');
      }

      setCurrentMatch(currentMatch + 1);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  if (matches.length === 0) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  if (currentMatch >= matches.length) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gradient-to-r from-blue-500 to-purple-500">
        <div className="text-white text-center">
          <h2 className="text-3xl font-bold mb-4">No more matches!</h2>
          <button onClick={() => window.location.reload()} className="bg-white text-purple-500 px-6 py-3 rounded-lg">
            Refresh for more</button>
        </div>
      </div>
    );
  }

  const match = matches[currentMatch];

  return (
    <div className="min-h-screen bg-gradient-to-r from-pink-400 to-red-400 flex items-center justify-center">
      <div className="bg-white rounded-2xl shadow-2xl max-w-sm w-full mx-4 overflow-hidden">
        <div className="h-96 bg-gray-200 flex items-center justify-center">
          <span className="text-6xl">üì∏</span>
        </div>
        <div className="p-6">
          <h2 className="text-2xl font-bold mb-2">{match.firstName} {match.lastName}</h2>
          <p className="text-gray-600 mb-1">Age: {match.age}</p>
          <p className="text-gray-600 mb-1">Major: {match.major}</p>
          <p className="text-gray-600 mb-1">Year: {match.year}</p>
          <p className="text-gray-700 mb-4">{match.bio || 'No bio available'}</p>
          <div className="flex justify-center space-x-4">
            <button onClick={() => handleAction('dislike')}
              className="bg-red-500 text-white px-8 py-3 rounded-full text-lg hover:bg-red-600">
              ‚ùå Pass</button>
            <button onClick={() => handleAction('like')}
              className="bg-green-500 text-white px-8 py-3 rounded-full text-lg hover:bg-green-600">
              üíñ Like</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
